/**
 * @name caption_node
 */
export const caption = {
  content: '(text|hardBreak|mention|emoji|date|placeholder|inlineCard|status)*',
  isolating: true,
  marks: '_',
  selectable: false,
  parseDOM: [{
    tag: 'figcaption[data-caption]'
  }],

  toDOM(node) {
    const attrs = {
      'data-caption': 'true'
    };
    return ['figcaption', attrs, 0];
  }

};